package testpkg01;

public class Greatest3Nos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=50;
		int b=60;
		int c=123;
		
		if ((a>b)&&(a>c)){
			System.out.println("A is greater");
		} else if (b>c) {
			System.out.println("B is greater");
		} else {
			System.out.println("C is greater");
		}

	}

}
